import{_ as l,o as i,c as a,e}from"./app.41cf67d2.js";const p=JSON.parse('{"title":"面试前的准备","description":"","frontmatter":{},"headers":[],"relativePath":"pages/interview/foundation/interview-foundation-2.md","filePath":"pages/interview/foundation/interview-foundation-2.md","lastUpdated":1699250630000}'),t={name:"pages/interview/foundation/interview-foundation-2.md"},o=e('<h1 id="面试前的准备" tabindex="-1">面试前的准备 <a class="header-anchor" href="#面试前的准备" aria-label="Permalink to &quot;面试前的准备&quot;">​</a></h1><h2 id="_1-面试的环境和流程" tabindex="-1">1. 面试的环境和流程 <a class="header-anchor" href="#_1-面试的环境和流程" aria-label="Permalink to &quot;1. 面试的环境和流程&quot;">​</a></h2><h3 id="_1-1-什么是面试" tabindex="-1">1.1. 什么是面试 <a class="header-anchor" href="#_1-1-什么是面试" aria-label="Permalink to &quot;1.1. 什么是面试&quot;">​</a></h3><ul><li>经过组织者精心设计</li><li>以交谈和观察为主要手段</li><li>评价知识、能力和经验、综合素质</li></ul><h3 id="_1-2-如何拿到简历" tabindex="-1">1.2. 如何拿到简历 <a class="header-anchor" href="#_1-2-如何拿到简历" aria-label="Permalink to &quot;1.2. 如何拿到简历&quot;">​</a></h3><ul><li>内推</li><li>猎头</li><li>hr收集（主动搜索，接收邮件）</li></ul><h3 id="_1-3-面试流程" tabindex="-1">1.3. 面试流程 <a class="header-anchor" href="#_1-3-面试流程" aria-label="Permalink to &quot;1.3. 面试流程&quot;">​</a></h3><blockquote><p>正规，部门比较齐全的公司，中大型厂</p></blockquote><ul><li>简历到hr手里，简短地约面试</li><li>一面 <ul><li>基础技术知识 <ul><li>面你的基本是一线的基础工程师</li></ul></li></ul></li><li>二面(交叉面试) <ul><li>以基础技术知识开始，到框架原理，到计算机的原理，再到业务场景的考察（项目经验、项目解决方案） <ul><li>面你的基本是小组组长级别</li><li>最少一轮，最多三到五轮</li><li>交叉面试意思是，一些大厂为了保证招聘到的候选人确实符合要求，找不同部门的面试官去相互交叉面试</li></ul></li></ul></li><li>三面 <ul><li>可能会问一下你项目经验的问题</li><li>更多的是讲一下你在这个组里的角色定位，接下来要做什么产品，对这个产品有什么想法等，如何对身边人进行技术提升……</li><li>这个奠定了你在你的leader心中的初步印象 <ul><li>面你的基本是项目leader</li></ul></li></ul></li><li>hr面试 <ul><li>为人低调，不要太作</li><li>ali中hr有一票否决权(不符合价值观)</li></ul></li><li>校招和社招区别： <ul><li>校招看中基础知识能力，主要在一面，一面过了其他差不多了</li><li>社招看中经验，主要在二面(基础知识要过关)</li><li>社招，工作时间越长，越偏重经验</li></ul></li></ul><h2 id="_2-jd分析" tabindex="-1">2. JD分析 <a class="header-anchor" href="#_2-jd分析" aria-label="Permalink to &quot;2. JD分析&quot;">​</a></h2><blockquote><p>即用人单位招聘需求的分析</p></blockquote><ul><li>工作内容</li><li>技术栈</li><li>经验要求</li><li>不要过于在意JD <ul><li>JD是hr发的</li><li>hr和技术人员可能沟通不及时</li><li>不能完全相信JD要求</li></ul></li></ul>',12),r=[o];function n(u,d,h,s,_,c){return i(),a("div",null,r)}const b=l(t,[["render",n]]);export{p as __pageData,b as default};
